SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS operacao_estoque_atual ON operacao_estoque CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_operacao_estoque_atual() RETURNS trigger AS $BODY$
declare

qt_movto_w	numeric(20);

BEGIN
select	count(*)
into STRICT	qt_movto_w
from	movimento_estoque
where	cd_operacao_estoque = NEW.cd_operacao_estoque;

if (qt_movto_w > 0) then
	BEGIN
	if (OLD.ie_entrada_saida <> NEW.ie_entrada_saida) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(181825,'DS_CAMPO=Entrada/Saída');
	end if;

	if (OLD.ie_atualiza_estoque <> NEW.ie_atualiza_estoque) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(181825,'DS_CAMPO=Atualiza Estoque Contábil');
	end if;

	if (coalesce(OLD.ie_consignado,0) <> coalesce(NEW.ie_consignado,0)) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(181825,'DS_CAMPO=Tipo consignado');
	end if;

	if (OLD.ie_tipo_requisicao <> NEW.ie_tipo_requisicao) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(181825,'DS_CAMPO=Tipo operação');
	end if;
	end;
end if;

if (NEW.ie_desconsidera_cons_ressup is null) then
	NEW.ie_desconsidera_cons_ressup := 'N';
end if;

if (coalesce(NEW.ie_situacao,'A') = 'A') then
	BEGIN
	if (NEW.ie_entrada_saida = 'E') then
		BEGIN
		if (coalesce(NEW.ie_consignado,0) in (5,8)) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(179789);
			/*r.aise_application_error(-20011,'A operação de consignado não é de entrada ');*/

		end if;
		if (coalesce(NEW.ie_tipo_requisicao,0) in (1,4,9,0)) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(179794);
			/*r.aise_application_error(-20011,'Estas operações são de saida (Não combinam com o tipo de operação) ');*/

		end if;
		end;
	else
		BEGIN
		if (coalesce(NEW.ie_consignado,0) in (1,2,3)) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(179795);
			/*r.aise_application_error(-20011,'A operação de consignado não é de entrada ');*/

		end if;
		if (coalesce(NEW.ie_tipo_requisicao,0) in (6,7,3)) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(179796);
			/*r.aise_application_error(-20011,'Estas operações são de entrada (Não combinam com o tipo de operação)');*/

		end if;
		end;
	end if;

	if (coalesce(NEW.ie_tipo_requisicao,0) in (2,21))  and (NEW.cd_operacao_correspondente is null) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(179797);
		/*r.aise_application_error(-20011,'Para Operação de Transferência é necessário informar Operação Corresp. !');*/

	end if;

	if (NEW.cd_operacao_correspondente is not null) and (coalesce(NEW.ie_tipo_requisicao,0) not in (2,21))  then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(179798);
		/*r.aise_application_error(-20011,'Este Tipo de Operação não pode ter Operação Corresp. !');*/

	end if;

	if (coalesce(NEW.ie_tipo_requisicao,0) = 2)  and (coalesce(NEW.ie_consignado,0) not in (0,6)) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(179799);
		/*r.aise_application_error(-20011,'Tipo Operação e Tipo Consignado não conferem !');*/

	end if;

	if (coalesce(NEW.ie_consignado,0) = 6) and (coalesce(NEW.ie_tipo_requisicao,2) not in (2,21))  then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(179799);
		/*r.aise_application_error(-20011,'Tipo Operação e Tipo Consignado não conferem !');*/

	end if;

	if (coalesce(NEW.ie_consignado,0) <> 0) and (NEW.ie_atualiza_estoque = 'S') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(179801);
		/*r.aise_application_error(-20011,'As Operações de consignado não devem atualizar estoque normal ');*/

	end if;
	end;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_operacao_estoque_atual() FROM PUBLIC;

CREATE TRIGGER operacao_estoque_atual
	BEFORE INSERT OR UPDATE ON operacao_estoque FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_operacao_estoque_atual();

