DROP TRIGGER IF EXISTS ordem_compra_insert_tie ON ordem_compra CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ordem_compra_insert_tie() RETURNS trigger AS $BODY$
declare
  ds_retorno_integracao_w    text;
  json_w                     philips_json;
  json_data_w                text;
BEGIN
  if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S') then
    if (wheb_usuario_pck.get_nm_usuario is not null) then
      json_w := philips_json();

      json_w.put('id', NEW.NR_ORDEM_COMPRA);
      json_w.put('supplier', NEW.CD_CGC_FORNECEDOR);
      json_w.put('buyer', NEW.CD_COMPRADOR);
      json_w.put('paymentTerm', NEW.CD_CONDICAO_PAGAMENTO);
      json_w.put('paymentForm', NEW.NR_SEQ_FORMA_PAGTO);
      json_w.put('establishment', NEW.CD_ESTABELECIMENTO);
      json_w.put('deliverySite', NEW.CD_LOCAL_ENTREGA);
      json_w.put('currency', NEW.CD_MOEDA);
      json_w.put('freightType', NEW.IE_FRETE);
      json_w.put('settlementDate', NEW.DT_BAIXA);
      json_w.put('inclusionDate', NEW.DT_INCLUSAO);
      json_w.put('releaseDate', NEW.DT_LIBERACAO);
      json_w.put('orderDate', NEW.DT_ORDEM_COMPRA);
      json_w.put('externalCode', NEW.NR_DOCUMENTO_EXTERNO);
      json_w.put('status', NEW.IE_SITUACAO);
      json_w.put('prescription', NEW.NR_PRESCRICAO);
      json_w.put('transporter', NEW.CD_CGC_TRANSPORTADOR);
      json_w.put('requester', NEW.CD_PESSOA_SOLICITANTE);
      json_w.put('naturalPerson', NEW.CD_PESSOA_FISICA);
      json_w.put('type', NEW.IE_TIPO_ORDEM);
      json_w.put('internalSequence', NEW.NR_SEQ_INTERNO);
      json_w.put('internalProcess', NEW.NR_PROCESSO_INTERNO);
      json_w.put('notes', NEW.DS_OBSERVACAO);
      json_w.put('importationCertificate', NEW.NR_DECLARACAO_IMP);
      json_w.put('approvalDate', NEW.DT_APROVACAO);
      json_w.put('issueDate', NEW.DT_EMISSAO);
      json_w.put('readingDate', NEW.DT_LEITURA);
      json_w.put('deliveryDate', NEW.DT_ENTREGA);
      json_w.put('deliveryConfirmationDate', NEW.DT_CONFIRMA_ENTREGA);
      json_w.put('schedule', NEW.NR_SEQ_AGENDA_PAC);
      json_w.put('insurance', NEW.CD_CONVENIO);
      json_w.put('encounter', NEW.NR_ATENDIMENTO);
      json_w.put('cancellation', NEW.NR_SEQ_MOTIVO_CANCEL);
      json_w.put('urgent', NEW.IE_URGENTE);
      json_w.put('portalAcceptanceDate', NEW.DT_ACEITE);
      json_w.put('submittedPortal', NEW.IE_LIBERACAO_PORTAL);
      json_w.put('notifyMaterialArrival', NEW.IE_AVISO_CHEGADA);
      json_w.put('issueNotes', NEW.IE_EMITE_OBS);
      json_w.put('lastUpdate', NEW.DT_ATUALIZACAO);
      json_w.put('lastUpdateBy', NEW.NM_USUARIO);

      dbms_lob.createtemporary(json_data_w, true);
      json_w.(json_data_w);

      ds_retorno_integracao_w := bifrost.send_integration_content('purchaseorder.send', json_data_w, wheb_usuario_pck.get_nm_usuario);

    end if;
  end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ordem_compra_insert_tie() FROM PUBLIC;

