SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ordem_compra_item_entr_update ON ordem_compra_item_entrega CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ordem_compra_item_entr_update() RETURNS trigger AS $BODY$
declare
qt_reg_w			bigint;
nr_seq_nf_w		bigint;
nr_item_nf_w		bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if (OLD.dt_prevista_entrega is not null) and (OLD.dt_prevista_entrega <> NEW.dt_prevista_entrega) then
	BEGIN

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_nf_w
	from	nota_fiscal_item a,
		nota_fiscal b
	where	a.nr_sequencia = b.nr_sequencia
	and	b.dt_atualizacao_estoque is null
	and	a.nr_ordem_compra = NEW.nr_ordem_compra
	and	a.nr_item_oci = NEW.nr_item_oci
	and	trunc(a.dt_entrega_ordem) = trunc(OLD.dt_prevista_entrega);

	if (nr_seq_nf_w > 0) then
		BEGIN

		select	coalesce(max(a.nr_item_nf),0)
		into STRICT	nr_item_nf_w
		from	nota_fiscal_item a,
			nota_fiscal b
		where	a.nr_sequencia = b.nr_sequencia
		and	b.nr_sequencia = nr_seq_nf_w
		and	a.nr_ordem_compra = NEW.nr_ordem_compra
		and	a.nr_item_oci = NEW.nr_item_oci
		and	trunc(a.dt_entrega_ordem) = trunc(OLD.dt_prevista_entrega);

		if (nr_item_nf_w > 0) then
			BEGIN

			update	nota_fiscal_item
			set	dt_entrega_ordem = NEW.dt_prevista_entrega
			where	nr_sequencia = nr_seq_nf_w
			and	nr_item_nf = nr_item_nf_w;

			end;
		end if;

		end;
	end if;
	end;
end if;

<<Final>>
qt_reg_w	:= 0;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ordem_compra_item_entr_update() FROM PUBLIC;

CREATE TRIGGER ordem_compra_item_entr_update
	BEFORE UPDATE ON ordem_compra_item_entrega FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ordem_compra_item_entr_update();

