DROP TRIGGER IF EXISTS ordem_compra_venc_after_insert ON ordem_compra_venc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ordem_compra_venc_after_insert() RETURNS trigger AS $BODY$
declare

qt_reg_w		bigint := 0;


pragma autonomous_transaction;
BEGIN

/* Grava o agendamento da informação para atualização do fluxo de caixa. */

CALL gravar_agend_fluxo_caixa(NEW.nr_ordem_compra,NEW.NR_ITEM_OC_VENC,'OC', NEW.dt_vencimento,'I',NEW.nm_usuario);

select count(*)
into STRICT qt_reg_w
from ORDEM_COMPRA_ITEM_ENTREGA a
where a.nr_ordem_compra = NEW.nr_ordem_compra;

if (qt_reg_w > 0) then
		CALL reagendar_item_oc_fluxo(NEW.nr_ordem_compra, 'OC', 'B', NEW.nm_usuario);
end if;
commit;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
