SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS orientacao_alta_enf_pend_atual ON orientacao_alta_enf CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_orientacao_alta_enf_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w			smallint;
ie_tipo_w			varchar(10);
ie_lib_orientacao_alta_w	varchar(5);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	coalesce(max(ie_lib_orientacao_alta),'N')
into STRICT	ie_lib_orientacao_alta_w
from	parametro_medico
where	cd_estabelecimento = obter_estabelecimento_ativo;

if (ie_lib_orientacao_alta_w = 'S') then
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'OAE';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XOAE';
	end if;

	if (ie_tipo_w	is not null) and (NEW.IE_TIPO_REGISTRO <> 'C') then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, obter_pessoa_atendimento(NEW.nr_atendimento,'C'), NEW.nr_atendimento, NEW.nm_usuario);
	end if;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_orientacao_alta_enf_pend_atual() FROM PUBLIC;

CREATE TRIGGER orientacao_alta_enf_pend_atual
	AFTER INSERT OR UPDATE ON orientacao_alta_enf FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_orientacao_alta_enf_pend_atual();

