DROP TRIGGER IF EXISTS paciente_atendimento_insert ON paciente_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_paciente_atendimento_insert() RETURNS trigger AS $BODY$
declare
cd_pessoa_atend_w varchar(10);
cd_pessoa_qui_w varchar(10);
cd_estabelecimento_w smallint;
BEGIN



if (NEW.nr_atendimento is not null) then
	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_atend_w
	from	atendimento_paciente
	where	nr_atendimento	= NEW.nr_atendimento;

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_qui_w
	from	paciente_setor
	where	nr_seq_paciente	= NEW.nr_seq_paciente;

	if (cd_pessoa_atend_w is not null) and (cd_pessoa_qui_w is not null) and (cd_pessoa_atend_w	<> cd_pessoa_qui_w) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(197786);
	end if;
end if;

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	paciente_setor
where	nr_seq_paciente	= NEW.nr_seq_paciente;

if (NEW.cd_estabelecimento is null) then
	NEW.cd_estabelecimento := coalesce(cd_estabelecimento_w,wheb_usuario_pck.get_cd_estabelecimento);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_paciente_atendimento_insert() FROM PUBLIC;

CREATE TRIGGER paciente_atendimento_insert
	BEFORE INSERT ON paciente_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_paciente_atendimento_insert();

