SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS paciente_atend_pend_atual ON paciente_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_paciente_atend_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w		smallint;
ie_tipo_w		varchar(10);
ie_libera_ciclo_onc_w	varchar(10);
cd_pessoa_fisica_w		varchar(10);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if (wheb_usuario_pck.get_cd_funcao = 281) then

	select	max(IE_LIBERA_CICLO_ONC)
	into STRICT	ie_libera_ciclo_onc_w
	from	parametro_medico
	where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

	if (coalesce(ie_libera_ciclo_onc_w,'N') = 'S') then
		if (NEW.dt_liberacao_reg is null) then
			ie_tipo_w := 'ONC';
		elsif (OLD.dt_liberacao_reg is null) and (NEW.dt_liberacao_reg is not null) then
			ie_tipo_w := 'XONC';
		end if;
		
		if (NEW.dt_cancelamento is not null) then		
			
			ie_tipo_w := '';

			delete	from   pep_item_pendente
			where  nr_seq_atend_ciclo  = NEW.nr_seq_atendimento;			
				
		end if;
	
		if (ie_tipo_w	is not null) then
		
			Select 	max(cd_pessoa_fisica)
			into STRICT	cd_pessoa_fisica_w
			from	paciente_setor
			where	nr_seq_paciente = NEW.nr_seq_paciente;

      CALL Gerar_registro_pendente_PEP( cd_tipo_registro_p => ie_tipo_w,
        nr_sequencia_registro_p => NEW.nr_seq_atendimento,
        cd_pessoa_fisica_p => coalesce(cd_pessoa_fisica_w,substr(obter_pessoa_atendimento(NEW.nr_atendimento,'C'),1,255)), 
        nr_atendimento_p => NEW.nr_atendimento,
        nm_usuario_p => NEW.nm_usuario,
        nr_atend_cons_pepa_p => NEW.nr_seq_atend_cons_pepa);
		end if;
	end if;

end if;
	
<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_paciente_atend_pend_atual() FROM PUBLIC;

CREATE TRIGGER paciente_atend_pend_atual
	AFTER INSERT OR UPDATE ON paciente_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_paciente_atend_pend_atual();

