SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS paciente_exame_atual ON paciente_exame CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_paciente_exame_atual() RETURNS trigger AS $BODY$
declare
NR_ATENDIMENTO_W                    bigint;

BEGIN
if (coalesce(OLD.DT_REGISTRO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_REGISTRO) and (NEW.DT_REGISTRO is not null) then
	NEW.ds_utc		:= obter_data_utc(NEW.DT_REGISTRO, 'HV');	
end if;

if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
	NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');

end if;

if	(((coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) and (NEW.DS_RESULTADO is not null)) or
	((NEW.DT_LIBERACAO is not null) and (NEW.DS_RESULTADO is not null) and (coalesce(OLD.DS_RESULTADO,'XPTO') <> NEW.DS_RESULTADO))) then
	
	
	--(:new.DS_RESULTADO is not null) and

	--(:old.DS_RESULTADO <> :new.DS_RESULTADO))

	if (NEW.IE_EXAME_PAC = 'COVID-19') then
		select 	max(nr_atendimento)
		into STRICT	NR_ATENDIMENTO_W
		from	atendimento_paciente
		where 	cd_pessoa_fisica = NEW.CD_PESSOA_FISICA;
		
		CALL gerar_evolucao_exameRapido(NR_ATENDIMENTO_W, NEW.NM_USUARIO,'EXA', obter_desc_expressao(964679)||': ' ||NEW.DS_RESULTADO ||chr(10)||chr(13)||coalesce(NEW.ds_observacao,OLD.ds_observacao), NEW.CD_PESSOA_FISICA);

	end if;
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_paciente_exame_atual() FROM PUBLIC;

CREATE TRIGGER paciente_exame_atual
	BEFORE INSERT OR UPDATE ON paciente_exame FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_paciente_exame_atual();

