DROP TRIGGER IF EXISTS paciente_rep_prescricao_atual ON paciente_rep_prescricao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_paciente_rep_prescricao_atual() RETURNS trigger AS $BODY$
declare
BEGIN

if (NEW.nr_seq_restricao is not null) and (NEW.dt_fim is not null) and (OLD.dt_fim is null or OLD.dt_fim <> NEW.dt_fim)
	then
		NEW.nm_usuario_termino := wheb_usuario_pck.get_nm_usuario;
end if;

if (NEW.dt_fim is null) and (OLD.dt_fim is not null)then
	NEW.nm_usuario_termino := '';
end if;

if (coalesce(OLD.DT_ATUALIZACAO_NREC,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_ATUALIZACAO_NREC) and (NEW.DT_ATUALIZACAO_NREC is not null) then
	NEW.ds_utc		:= obter_data_utc(NEW.DT_ATUALIZACAO_NREC, 'HV');
end if;

if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
	NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
end if;

if (NEW.DT_INICIO is not null) and (NEW.DT_FIM is not null) and (NEW.DT_INICIO > NEW.DT_FIM	 ) then
	CALL wheb_mensagem_pck.EXIBIR_MENSAGEM_ABORT(23525);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
