SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS paciente_vacina_atual ON paciente_vacina CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_paciente_vacina_atual() RETURNS trigger AS $BODY$
declare

qt_dose_unica_w smallint;
dt_prevista_vacina_w timestamp;
BEGIN
  BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
	
	if (coalesce(OLD.DT_REGISTRO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_REGISTRO) and (NEW.DT_REGISTRO is not null) then
		NEW.ds_utc		:= obter_data_utc(NEW.DT_REGISTRO, 'HV');	
	end if;

	if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
		NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
	end if;

	if (NEW.DT_PREVISTA_EXECUCAO is null and NEW.DT_REGISTRO is not null) then
		BEGIN
			select add_months(NEW.DT_REGISTRO, coalesce(max(QT_PROX_MES),0))
			into STRICT NEW.DT_PREVISTA_EXECUCAO
			from VACINA_CALENDARIO
			WHERE NR_SEQ_VACINA = NEW.NR_SEQ_VACINA
			and IE_DOSE = NEW.IE_DOSE;
		exception when no_data_found then
			NEW.DT_PREVISTA_EXECUCAO := null;
		end;
	end if;

end if;

  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_paciente_vacina_atual() FROM PUBLIC;

CREATE TRIGGER paciente_vacina_atual
	BEFORE INSERT OR UPDATE ON paciente_vacina FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_paciente_vacina_atual();

