DROP TRIGGER IF EXISTS paciente_vulnerabilidade_atual ON paciente_vulnerabilidade CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_paciente_vulnerabilidade_atual() RETURNS trigger AS $BODY$
DECLARE


BEGIN
										
if (NEW.DT_ATUALIZACAO_NREC is not null) then
	NEW.ds_utc		:= substr(obter_data_utc(NEW.DT_ATUALIZACAO_NREC, 'HV'),1,50);	
end if;

if	((OLD.DT_LIBERACAO is null) or (OLD.DT_LIBERACAO <> NEW.DT_LIBERACAO)) and (NEW.DT_LIBERACAO is not null) then
	NEW.ds_utc_atualizacao	:= substr(obter_data_utc(NEW.DT_LIBERACAO,'HV'),1,50);
end if;


RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
