CREATE TRIGGER paciente_vulnerabilidade_atual
	BEFORE INSERT OR UPDATE ON paciente_vulnerabilidade FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_paciente_vulnerabilidade_atual();

