SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pac_atend_interc_pend_atual ON paciente_atend_interc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pac_atend_interc_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w			smallint;
ie_tipo_w			varchar(10);
ie_lib_interc_w			varchar(10);
cd_pessoa_fisica_w		varchar(10);
nr_atendimento_w		bigint;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	max(ie_libera_intercorrencia)
into STRICT	ie_lib_interc_w
from	parametros_quimio;

select	max(nr_atendimento),
	max(obter_codigo_paciente_setor(nr_seq_paciente))
into STRICT	nr_atendimento_w,
	cd_pessoa_fisica_w
from	paciente_atendimento
where	nr_seq_atendimento	= NEW.nr_seq_atendimento;

if (coalesce(ie_lib_interc_w,'N') = 'S') then
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'QLI';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XQLI';
	end if;

	if (ie_tipo_w	is not null) then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, cd_pessoa_fisica_w, nr_atendimento_w, NEW.nm_usuario);
	end if;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pac_atend_interc_pend_atual() FROM PUBLIC;

CREATE TRIGGER pac_atend_interc_pend_atual
	AFTER INSERT OR UPDATE ON paciente_atend_interc FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pac_atend_interc_pend_atual();

