BEGIN

if (TG_OP = 'INSERT')  or (OLD.cd_estabelecimento <> NEW.cd_estabelecimento) or (OLD.cd_local_estoque <> NEW.cd_local_estoque) or (OLD.cd_material <> NEW.cd_material) or (OLD.qt_estoque_maximo <> NEW.qt_estoque_maximo) or (OLD.qt_estoque_minimo <> NEW.qt_estoque_minimo) then
	
