			NEW.cd_local_estoque,
			NEW.cd_material,
			NEW.qt_estoque_maximo,
			NEW.qt_estoque_minimo,
			NEW.nm_usuario);
end if;	
if (TG_OP = 'INSERT') then	
	CALL gerar_int_dankia_pck.dankia_disp_material(NEW.cd_material,NEW.cd_local_estoque,'I',NEW.nm_usuario);
	CALL gerar_int_dankia_pck.dankia_disp_padrao_local(NEW.cd_material,NEW.cd_local_estoque,NEW.qt_estoque_minimo,NEW.qt_estoque_maximo,'I',NEW.nr_seq_estrut_mat,NEW.nm_usuario);
elsif (TG_OP = 'UPDATE') then
	if (coalesce(NEW.cd_material,0) <> coalesce(OLD.cd_material,0)) then
		CALL gerar_int_dankia_pck.dankia_disp_material(OLD.cd_material,OLD.cd_local_estoque,'E',OLD.nm_usuario);
		CALL gerar_int_dankia_pck.dankia_disp_padrao_local(OLD.cd_material,OLD.cd_local_estoque,OLD.qt_estoque_minimo,OLD.qt_estoque_maximo,'E',OLD.nr_seq_estrut_mat,OLD.nm_usuario);

		CALL gerar_int_dankia_pck.dankia_disp_material(NEW.cd_material,NEW.cd_local_estoque,'I',NEW.nm_usuario);
		CALL gerar_int_dankia_pck.dankia_disp_padrao_local(NEW.cd_material,NEW.cd_local_estoque,NEW.qt_estoque_minimo,NEW.qt_estoque_maximo,'I',NEW.nr_seq_estrut_mat,NEW.nm_usuario);

	elsif (coalesce(NEW.cd_local_estoque,0) <> coalesce(OLD.cd_local_estoque,0)) or (coalesce(NEW.qt_estoque_minimo,0) <> coalesce(OLD.qt_estoque_minimo,0)) or (coalesce(NEW.qt_estoque_maximo,0) <> coalesce(OLD.qt_estoque_maximo,0)) or (coalesce(NEW.nr_seq_estrut_mat,0) <> coalesce(OLD.nr_seq_estrut_mat,0)) then
