CREATE OR REPLACE FUNCTION trigger_fct_padrao_estoque_local_tie() RETURNS trigger AS $BODY$
declare
  ds_retorno_integracao_w    text;
  json_w                     philips_json;
  json_data_w                text;
BEGIN
  if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S') then
    if (wheb_usuario_pck.get_nm_usuario is not null) then
      json_w := philips_json();

      json_w.put('stockLocation', NEW.CD_LOCAL_ESTOQUE);
      json_w.put('material', NEW.CD_MATERIAL);
      json_w.put('lastUpdate', NEW.DT_ATUALIZACAO);
      json_w.put('nameUser', NEW.NM_USUARIO);
      json_w.put('maximumStock', NEW.QT_ESTOQUE_MAXIMO);
      json_w.put('minimumStock', NEW.QT_ESTOQUE_MINIMO);
      json_w.put('attendLocation', NEW.CD_LOCAL_ATENDE);
      json_w.put('establishment', NEW.CD_ESTABELECIMENTO);
      json_w.put('status', NEW.IE_SITUACAO);
      json_w.put('countSequential', NEW.NR_SEQ_CONTAGEM);
      json_w.put('localization', NEW.DS_LOCALIZACAO);
      json_w.put('updateLocalDefault', NEW.IE_ATUALIZA_PADRAO_LOCAL);
      json_w.put('consumption', NEW.QT_CONSUMO);
      json_w.put('daysAdjustmentMinimum', NEW.QT_DIA_AJUSTE_MINIMO);
      json_w.put('daysAdjustmentMaximum', NEW.QT_DIA_AJUSTE_MAXIMO);
      json_w.put('daysConsumption', NEW.QT_DIA_CONSUMO);
      json_w.put('adjustmentMinimum', NEW.QT_MINIMO_AJUSTE);

      dbms_lob.createtemporary(json_data_w, true);
      json_w.(json_data_w);

      ds_retorno_integracao_w := bifrost.send_integration_content('stocklocationstandard.send', json_data_w, wheb_usuario_pck.get_nm_usuario);

    end if;
  end if;
RETURN NEW;
end
