SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS parto_insert_update ON parto CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_parto_insert_update() RETURNS trigger AS $BODY$
Declare
dt_entrada_w	timestamp;
qt_reg_w	smallint;
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;
select	dt_entrada
into STRICT	dt_entrada_w
from	atendimento_paciente
where	nr_atendimento = NEW.nr_atendimento;

if (NEW.dt_admissao is not null) and (NEW.dt_admissao < dt_entrada_w) then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266302);
end if;

/*
Guilherme Piaz em 26/08/2011 - OS 356221
if	(nvl(:new.ie_parto_normal,'N') = 'N') and
	(nvl(:new.ie_parto_cesaria,'N') = 'N') then

end if;
*/
/*
Dalcastagne em 18/04/2009 - OS 138199

if	(nvl(:new.ie_parto_normal,'N') = 'S') and
	(nvl(:new.ie_parto_cesaria,'N') = 'S') then

end if;
*/
if (coalesce(NEW.ie_parto_normal,'N') = 'S') and (coalesce(NEW.ie_laqueadura,'N') = 'S') then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266303);
end if;
if (coalesce(NEW.ie_parto_cesaria,'N') = 'S') and (coalesce(NEW.ie_parto_analgesia,'N') = 'S') then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266304);
end if;
if (coalesce(NEW.ie_parto_cesaria,'N') = 'S') and (coalesce(NEW.ie_parto_episio,'N') = 'S') then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266305);
end if;

if (NEW.dt_inicio_parto > LOCALTIMESTAMP) then
	BEGIN
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266306);
	end;
end if;

if (NEW.dt_admissao is not null) and (NEW.dt_admissao > LOCALTIMESTAMP) then
	BEGIN
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266307);
	end;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_parto_insert_update() FROM PUBLIC;

CREATE TRIGGER parto_insert_update
	BEFORE INSERT OR UPDATE ON parto FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_parto_insert_update();

