SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pat_cat_log_bef_delete ON patient_category_log CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pat_cat_log_bef_delete() RETURNS trigger AS $BODY$
declare
 qt_count_w	bigint:=0;
 qt_error_w	bigint:=0;
 pragma autonomous_transaction;

BEGIN

	if (wheb_usuario_pck.get_ie_executar_trigger = 'N') then
		qt_error_w:=0;
	else
	
		select trunc(max(dt_start_category))-trunc(OLD.dt_start_category)
		into STRICT qt_count_w
		from patient_category_log
		where nr_atendimento=OLD.nr_atendimento;

        if (qt_count_w > 0 ) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1217338); --You can only delete record with highest start category date.
		end if;
	
		select trunc(max(dt_start_category))-trunc(min(dt_start_category))
		into STRICT qt_count_w
		from patient_category_log
		where nr_atendimento=OLD.nr_atendimento;


		if (qt_count_w = 0 ) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1216371); --It is not advisable to delete the last record. There should be at least one record
		end if;
	end if;
RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
