DROP TRIGGER IF EXISTS pat_conta_alteracao_atual ON pat_conta_alteracao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pat_conta_alteracao_atual() RETURNS trigger AS $BODY$
DECLARE

ds_conta_contabil_w		varchar(255);
ie_conta_vigente_w		varchar(1);

BEGIN

select	substr(obter_se_conta_vigente(NEW.cd_conta_nova, NEW.dt_transacao),1,1)
into STRICT	ie_conta_vigente_w
;

if (ie_conta_vigente_w = 'N') then
	ds_conta_contabil_w	:= substr(obter_desc_conta_contabil(NEW.cd_conta_nova),1,255);
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266464,'CONTA=' || NEW.cd_conta_nova || ';' ||
						'DS_CONTA=' || ds_conta_contabil_w);
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pat_conta_alteracao_atual() FROM PUBLIC;

CREATE TRIGGER pat_conta_alteracao_atual
	BEFORE INSERT OR UPDATE ON pat_conta_alteracao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pat_conta_alteracao_atual();

