CREATE OR REPLACE FUNCTION trigger_fct_pat_local_atual() RETURNS trigger AS $BODY$
declare

ie_situacao_w	varchar(1);
cd_estab_centro_w	centro_custo.cd_estabelecimento%type;

BEGIN

if (NEW.cd_setor_atendimento is not null) then

	select	max(ie_situacao)
	into STRICT	ie_situacao_w
	from	setor_atendimento
	where	cd_setor_atendimento	= NEW.cd_setor_atendimento;

	if (ie_situacao_w = 'I') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(184179);

	end if;
end if;

if (coalesce(NEW.cd_centro_custo,0) <> 0) and (coalesce(NEW.cd_estabelecimento,0) <> 0) then
	BEGIN
	select	max(a.cd_estabelecimento)
	into STRICT	cd_estab_centro_w
	from	centro_custo a
	where	a.cd_centro_custo = NEW.cd_centro_custo;

	if (coalesce(cd_estab_centro_w,0) <> 0) and (coalesce(cd_estab_centro_w,0) <> NEW.cd_estabelecimento) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(673890);
	end if;

	end;
end if;

RETURN NEW;
end
