CREATE OR REPLACE FUNCTION trigger_fct_pat_tipo_bem_atual() RETURNS trigger AS $BODY$
declare

cd_conta_contabil_w		varchar(20);


BEGIN


if (NEW.nr_seq_regra_conta is not null) then

	select	max(cd_conta_contabil)
	into STRICT	cd_conta_contabil_w
	from	pat_conta_contabil
	where	nr_sequencia	= NEW.nr_seq_regra_conta;

	NEW.cd_conta_contabil	:= cd_conta_contabil_w;
else
	NEW.cd_conta_contabil	:= null;
end if;

RETURN NEW;
end
