SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pcs_mat_subst_ordem_update ON pcs_material_substituto CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pcs_mat_subst_ordem_update() RETURNS trigger AS $BODY$
declare

qt_existe_w	integer;

PRAGMA autonomous_transaction;

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pcs_material_substituto
where	cd_material = NEW.cd_material
and	nr_seq_regra = NEW.nr_seq_regra
and	nr_sequencia <> NEW.nr_sequencia;

if (qt_existe_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(341258, null);
	/* Esta material já esta cadastrado para a regra */

end if;

select	count(*)
into STRICT	qt_existe_w
from	pcs_material_substituto
where	nr_prioridade = NEW.nr_prioridade
and	ie_situacao = 'A'
and	nr_seq_regra = NEW.nr_seq_regra
and	nr_sequencia <> NEW.nr_sequencia;

if (qt_existe_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(341259, null);
	/* Está prioridade já existe para o item substituído*/

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pcs_mat_subst_ordem_update() FROM PUBLIC;

CREATE TRIGGER pcs_mat_subst_ordem_update
	BEFORE INSERT OR UPDATE ON pcs_material_substituto FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pcs_mat_subst_ordem_update();

