CREATE OR REPLACE FUNCTION trigger_fct_pep_acesso_atual() RETURNS trigger AS $BODY$
declare


BEGIN
if (NEW.DT_ATUALIZACAO_NREC is null) then
	NEW.DT_ATUALIZACAO_NREC	:= LOCALTIMESTAMP;
end if;
if (coalesce(OLD.DT_ATUALIZACAO_NREC,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_ATUALIZACAO_NREC) and (NEW.DT_ATUALIZACAO_NREC is not null) then
	NEW.ds_utc				:= obter_data_utc(NEW.DT_ATUALIZACAO_NREC, 'HV');
end if;

if (coalesce(OLD.DT_ATUALIZACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_ATUALIZACAO) and (NEW.DT_ATUALIZACAO is not null) then
	NEW.ds_utc_ATUALIZACAO			:= obter_data_utc(NEW.DT_ATUALIZACAO, 'HV');
end if;

RETURN NEW;
end
$BODY$
