SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pep_autor_acesso_atual ON pep_autor_acesso CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pep_autor_acesso_atual() RETURNS trigger AS $BODY$
declare

nr_seq_perfil_w		bigint;
ie_autoriza_w		varchar(5);

BEGIN

select	max(nr_seq_perfil)
into STRICT	nr_seq_perfil_w
from	pessoa_fisica
where	cd_pessoa_fisica = NEW.cd_paciente;

select	max(ie_autorizar_acesso)
into STRICT	ie_autoriza_w
from	pep_perfil_paciente
where	nr_sequencia = nr_seq_perfil_w;


if (coalesce(ie_autoriza_w,'S') = 'N') then
	--	Não é possível alterar a autorização de acesso para este perfil do paciente. Gestão de acessos PEP.#@#@
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264546);
end if;


RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pep_autor_acesso_atual() FROM PUBLIC;

CREATE TRIGGER pep_autor_acesso_atual
	BEFORE INSERT OR UPDATE ON pep_autor_acesso FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pep_autor_acesso_atual();

