SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pep_curativo_pend_atual ON cur_ferida CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pep_curativo_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w			smallint;
ie_tipo_w			varchar(10);
ie_pep_curativo_w			varchar(10);
cd_pessoa_fisica_w		varchar(10);
nr_atendimento_w		bigint;
ie_tipo_pendencia_w				varchar(1);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	max(ie_libera_curativo)
into STRICT	ie_pep_curativo_w
from	parametro_medico
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;


if (coalesce(ie_pep_curativo_w,'N') = 'S') then
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'CR';
		
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XCR'; -- Excluir Pendencia
		ie_tipo_pendencia_w := 'L'; -- de Liberacao
	
	elsif (OLD.nr_seq_assinatura is null) and (NEW.nr_seq_assinatura is not null) then
		ie_tipo_w := 'XCR'; -- Excluir Pendencia
		ie_tipo_pendencia_w := 'A'; -- de Assinatura
	end if;

	if (ie_tipo_w	is not null) or (ie_tipo_w <> '' )then

		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, NEW.cd_pessoa_fisica, NEW.nr_atendimento, NEW.nm_usuario, ie_tipo_pendencia_w,
			null, null, null, null, null, null, null, null, null, null, null, null, null, 281);
	end if;
end if;


<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pep_curativo_pend_atual() FROM PUBLIC;

CREATE TRIGGER pep_curativo_pend_atual
	AFTER INSERT OR UPDATE ON cur_ferida FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pep_curativo_pend_atual();

