SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pep_item_pendente_delete ON pep_item_pendente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pep_item_pendente_delete() RETURNS trigger AS $BODY$
DECLARE
qt_reg_w	smallint;
nr_sequencia_w	bigint;
nr_seq_compl_w pep_item_pendente_comp.nr_sequencia%type;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
	if (coalesce(OLD.ie_tipo_pendencia,'L') = 'A') then

		select 	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	pep_item_pend_assina
		where	nr_seq_pendencia = OLD.nr_sequencia;

		if (coalesce(nr_sequencia_w,0) > 0) then
			delete FROM pep_item_pend_assina
			where nr_sequencia = nr_sequencia_w;
		end if;

	end if;

	select max(nr_sequencia)
	into STRICT nr_seq_compl_w
	from pep_item_pendente_comp
	where nr_seq_item_pend = OLD.NR_SEQUENCIA;

	if (nr_seq_compl_w is not null) then
	  delete from pep_item_pendente_comp
	  where nr_seq_item_pend = OLD.NR_SEQUENCIA;
	end if;
end if;
RETURN OLD;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pep_item_pendente_delete() FROM PUBLIC;

CREATE TRIGGER pep_item_pendente_delete
	BEFORE DELETE ON pep_item_pendente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pep_item_pendente_delete();

