DROP TRIGGER IF EXISTS pep_orientacao_geral_insert ON pep_orientacao_geral CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pep_orientacao_geral_insert() RETURNS trigger AS $BODY$
declare
cd_pessoa_fisica_w	varchar(10);
BEGIN 
 
 
NEW.cd_setor_atendimento	:= Obter_Unidade_Atendimento(NEW.nr_atendimento,'IAA','CS');
 
 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	usuario 
where	nm_usuario	= NEW.nm_usuario;
 
if (cd_pessoa_fisica_w	is not null) then 
	NEW.cd_especialidade_medico	:= obter_especialidade_medico(cd_pessoa_fisica_w,'C');
end if;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
