CREATE OR REPLACE FUNCTION trigger_fct_pep_pac_ci_aft_upd() RETURNS trigger AS $BODY$
BEGIN

if	((OLD.DT_LIBERACAO is null and  NEW.DT_LIBERACAO is not null) and (coalesce(pkg_i18n.get_user_locale, 'pt_BR')='ja_JP')) then
