SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pep_pac_ci_pend_atual ON pep_pac_ci CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pep_pac_ci_pend_atual() RETURNS trigger AS $BODY$
declare

PRAGMA AUTONOMOUS_TRANSACTION;

qt_reg_w	               smallint;
ie_tipo_w		            varchar(10);
ie_lib_consentimento_w	varchar(10);
nr_atendimento_w  atendimento_paciente.nr_atendimento%type;
cd_convenio_w  Atend_Categoria_Convenio.cd_convenio%type;
cd_medico_resp_w varchar(10);
BEGIN
  BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

	if (TG_OP = 'INSERT') or (TG_OP = 'UPDATE') then
		select	max(IE_LIB_CONSENTIMENTO)
		into STRICT	ie_lib_consentimento_w
		from	parametro_medico
		where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

		if (coalesce(ie_lib_consentimento_w,'N') = 'S') then
			if (NEW.dt_liberacao is null) then
				ie_tipo_w := 'CNL';
			elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		       ie_tipo_w := 'XCNL';
			end if;

			BEGIN
			if (ie_tipo_w	is not null) then
				CALL Gerar_registro_pendente_PEP( cd_tipo_registro_p => ie_tipo_w,
          nr_sequencia_registro_p => NEW.nr_sequencia,
          cd_pessoa_fisica_p => NEW.cd_pessoa_fisica,
          nr_atendimento_p => NEW.nr_atendimento,
          nm_usuario_p => NEW.nm_usuario,
          nr_atend_cons_pepa_p => NEW.NR_SEQ_ATEND_CONS_PEPA);
			end if;
			exception
				when others then
				null;
			end;		
			
			if (NEW.ie_tipo_consentimento = 'L') and (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
			
			BEGIN
			
			select max(a.nr_atendimento),
				   max(cd_medico_resp)
			into STRICT   nr_atendimento_w,
				   cd_medico_resp_w
			from   atendimento_paciente a,
				   atend_categoria_convenio b,
				   conversao_meio_externo c
			where  a.nr_atendimento = b.nr_atendimento
			and    upper(c.cd_externo) = 'RESUNIMED'
			and	   upper(c.nm_tabela) = 'ATEND_CATEGORIA_CONVENIO'
			and    c.cd_interno = to_char(b.cd_convenio)			
			and    a.cd_pessoa_fisica = NEW.cd_pessoa_fisica;
			
			select OBTER_CONVENIO_ATENDIMENTO(nr_atendimento_w)
			into STRICT cd_convenio_w
			;
			
			--  Gerar RES UNIMED - 00790 - Ativacao do RES do beneficiario 

		    CALL gerar_transacao_res(nr_atendimento_w,'00790',obter_usuario_pessoa(cd_medico_resp_w),cd_convenio_w,'','',NEW.nr_sequencia);
			
			exception
				when others then
				null;
			end;
		end if;
	
	end if;		
		

	elsif (TG_OP = 'DELETE') then
            delete 	from pep_item_pendente
            where 	IE_TIPO_REGISTRO = 'CNL'
            and	      nr_seq_registro = OLD.nr_sequencia
            and	      coalesce(IE_TIPO_PENDENCIA,'L')	= 'L';
		
            commit;
   end if;

if (OLD.dt_inativacao is null and NEW.dt_inativacao is not null and NEW.cd_evolucao is not null) then

	delete from clinical_note_soap_data where cd_evolucao = NEW.cd_evolucao
	and ie_med_rec_type ='CONSENT'
	and ie_stage = 1
	and ie_soap_type = 'P'
	and nr_seq_med_item = NEW.nr_sequencia;
	end if;
	commit;

<<Final>>
qt_reg_w	:= 0;
  END;
IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pep_pac_ci_pend_atual() FROM PUBLIC;

CREATE TRIGGER pep_pac_ci_pend_atual
	AFTER INSERT OR UPDATE OR DELETE ON pep_pac_ci FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pep_pac_ci_pend_atual();

