SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pessoa_documentacao_befinsert ON pessoa_documentacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pessoa_documentacao_befinsert() RETURNS trigger AS $BODY$
declare
nr_seq_ageint_w	agenda_integrada.nr_sequencia%type;
ie_existe_w	varchar(1);
BEGIN
  BEGIN

if (coalesce(NEW.nr_seq_ageint,0) > 0) then

	select	max(a.nr_seq_agenda_int)
	into STRICT	nr_seq_ageint_w
	from	agenda_integrada_item a,
		agenda_paciente b
	where 	a.nr_seq_agenda_exame = b.nr_sequencia
	and	b.nr_sequencia = NEW.nr_seq_ageint;

	if (coalesce(nr_seq_ageint_w,0) > 0) then

		BEGIN
			select 'S'
			into STRICT	ie_existe_w
			from 	agenda_integrada
			where 	nr_sequencia = nr_seq_ageint_w  LIMIT 1;

			NEW.nr_seq_ageint := nr_seq_ageint_w;

		exception
		when others then
			ie_existe_w := 'N';
		end;

	end if;

end if;

  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pessoa_documentacao_befinsert() FROM PUBLIC;

CREATE TRIGGER pessoa_documentacao_befinsert
	BEFORE INSERT ON pessoa_documentacao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pessoa_documentacao_befinsert();

