DROP TRIGGER IF EXISTS pessoa_fisica_delegacao_delete ON pessoa_fisica_delegacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pessoa_fisica_delegacao_delete() RETURNS trigger AS $BODY$
DECLARE
 
ds_log_w	varchar(4000);
 
BEGIN 
 
ds_log_w 	:= substr(wheb_mensagem_pck.get_texto(796662,'DS_OBJETIVO_W=' || obter_valor_dominio(2106,OLD.ie_objetivo) || ';' 
				|| 'DS_PESSOA_FISICA_W=' || OLD.cd_pessoa_fisica || ' - ' || obter_nome_pf(OLD.cd_pessoa_fisica) || ';' 
				|| 'DS_SUBSTITUTO_W=' || OLD.cd_pessoa_substituta || ' - ' || obter_nome_pf(OLD.cd_pessoa_substituta) || ';' 
				|| 'DT_LIMITE_W=' || OLD.dt_limite),1,4000);
			 
			 
insert into pessoa_fisica_deleg_log( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	ds_titulo, 
	ds_log, 
	cd_pessoa_substituida, 
	cd_pessoa_delegada) 
values (	nextval('pessoa_fisica_deleg_log_seq'), 
	LOCALTIMESTAMP, 
	WHEB_USUARIO_PCK.get_nm_usuario, 
	LOCALTIMESTAMP, 
	WHEB_USUARIO_PCK.get_nm_usuario, 
	wheb_mensagem_pck.get_texto(796663),--'Exclusão delegação', 
	ds_log_w, 
	OLD.cd_pessoa_fisica, 
	OLD.cd_pessoa_substituta);	
 
RETURN OLD;
end
