DROP TRIGGER IF EXISTS pessoa_fisica_delete ON pessoa_fisica CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pessoa_fisica_delete() RETURNS trigger AS $BODY$
declare
reg_integracao_p	gerar_int_padrao.reg_integracao;

BEGIN
reg_integracao_p.ie_operacao	:=	'E';
reg_integracao_p.nr_prontuario	:=	OLD.nr_prontuario;
reg_integracao_p.cd_pessoa_fisica	:=	OLD.cd_pessoa_fisica;

SELECT CASE WHEN COUNT(*)=0 THEN  'N'  ELSE 'S' END
INTO STRICT reg_integracao_p.ie_pessoa_atend 
FROM pessoa_fisica_aux
WHERE cd_pessoa_fisica = OLD.cd_pessoa_fisica
AND NR_PRIMEIRO_ATEND IS NOT NULL;

if OLD.nr_cpf is not null then
   reg_integracao_p.ie_possui_cpf := 'S';
end if;

if (wheb_usuario_pck.get_ie_lote_contabil = 'N') then
reg_integracao_p := gerar_int_padrao.gravar_integracao('12', OLD.cd_pessoa_fisica, OLD.nm_usuario, reg_integracao_p);
end if;

delete FROM pessoa_fisica_aux
WHERE cd_pessoa_fisica = OLD.cd_pessoa_fisica;

RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
