DROP TRIGGER IF EXISTS pessoa_fis_conta_pix_before ON pessoa_fis_conta_pix CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pessoa_fis_conta_pix_before() RETURNS trigger AS $BODY$
declare
    qt_preferencial_w smallint;
    pragma autonomous_transaction;
BEGIN
    if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

        CALL pix_pck.valida_chave( ds_chave_p => NEW.ds_chave,
                              ie_tipo_chave_p => NEW.ie_tipo_chave);

        if (NEW.ie_preferencial = 'S') then
            BEGIN
                update pessoa_fis_conta_pix a
                set    a.ie_preferencial = 'N'
                where  a.cd_pessoa_fisica = NEW.cd_pessoa_fisica
                and    a.nr_sequencia <> NEW.nr_sequencia;
                commit;
            end;
        else
            select count(1)
            into STRICT   qt_preferencial_w
            from   pessoa_fis_conta_pix a
            where  a.cd_pessoa_fisica = NEW.cd_pessoa_fisica
            and    a.ie_preferencial = 'S';

            if (qt_preferencial_w = 0) then
                NEW.ie_preferencial := 'S';
            end if;
        end if;
    end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
