DROP TRIGGER IF EXISTS pessoa_juridica_seg_before ON pessoa_juridica CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pessoa_juridica_seg_before() RETURNS trigger AS $BODY$
declare

cd_cgc_w	pessoa_juridica.cd_cgc%type;

BEGIN
if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S') then

	select	max(cd_cgc)
	into STRICT	cd_cgc_w
	from	estabelecimento
	where	ie_situacao = 'A'
	and 	cd_cgc in (	'00000000000007', '00000000000009', '00000000000010',
				'00000000000013', '00000000000017', '00000000000018',
				'00000000000019', '00000000000020', '00000000000021',
				'00000000000022', '00000000000023', '00000000000024',
				'00000000000025', '00000000000027', '00000000000028',
				'00000000000029', '00000000000031', '00000000000032',
				'00000000002231');

	if	((pkg_i18n.get_user_locale <> 'pt_BR') or (cd_cgc_w is not null)) then
	
		if (coalesce(NEW.ds_senha, 'XX') <> coalesce(OLD.ds_senha, 'XX')) then
			NEW.ds_senha:= wheb_seguranca.encrypt(NEW.ds_senha, 'XxPessoaJudidicaxX');
		end if;
		
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
