      json_w.put('creationUser', NEW.NM_USUARIO_NREC);
      json_w.put('ansCode', NEW.CD_ANS);
      json_w.put('maintainerCode', NEW.CD_CGC_MANTENEDORA);
      json_w.put('cnes', NEW.CD_CNES);
