      json_w.put('createadAt', NEW.DT_CRIACAO);
      json_w.put('lastRevision', NEW.DT_ULTIMA_REVISAO);
      json_w.put('isIndividualEntrepreneur', NEW.IE_EMPREENDEDOR_INDIVIDUAL);
      json_w.put('revisionMethod', NEW.IE_FORMA_REVISAO);
      json_w.put('quality', NEW.IE_QUALIDADE);
      json_w.put('senderStatus', NEW.IE_STATUS_ENVIO);
      json_w.put('sendingStatus', NEW.IE_STATUS_EXPORTAR);
      json_w.put('municipalityTaxType', NEW.IE_TIPO_TRIB_MUNICIPAL);
      json_w.put('taxType', NEW.IE_TIPO_TRIBUTACAO);
      json_w.put('isTransportation', NEW.IE_TRANSPORTE);
      json_w.put('contactName', NEW.NM_PESSOA_CONTATO);
      json_w.put('revisionUserName', NEW.NM_USUARIO_REVISAO);
      json_w.put('producerOrManufacturer = false', NEW.IE_PROD_FABRIC);
      json_w.put('status = StateType.ACTIVE', NEW.IE_SITUACAO);
      json_w.put('originCountry', NEW.NR_SEQ_PAIS);
      json_w.put('operationAuth', NEW.NR_AUTOR_FUNC);
      json_w.put('operationAuthExpDate', NEW.DT_VALIDADE_AUTOR_FUNC);
      json_w.put('holder', NEW.CD_PORTADOR);
      json_w.put('holderType', NEW.CD_TIPO_PORTADOR);
      json_w.put('naturalPersonResponsibleTechnician', NEW.CD_PF_RESP_TECNICO);
      json_w.put('legalEntityType', NEW.CD_TIPO_PESSOA);
      json_w.put('contributionType', NEW.IE_TIPO_CONTRIBUICAO);

      dbms_lob.createtemporary(json_data_w, true);
      json_w.(json_data_w);

      ds_retorno_integracao_w := bifrost.send_integration_content('legalentity.send', json_data_w, wheb_usuario_pck.get_nm_usuario);

    end if;
  end if;
