SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pe_prescricao_pend_atual ON pe_prescricao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pe_prescricao_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w	smallint;
ie_tipo_w	varchar(10);
nm_usuario_w	varchar(15);
ie_tipo_reg_w	varchar(10);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if (NEW.dt_liberacao is null) then

	if (coalesce(NEW.IE_AVALIADOR_AUX,'N') = 'S') and (NEW.DT_LIBERACAO_AUX is not null)then

			select 	obter_usuario_pf(NEW.cd_prescritor)
			into STRICT	nm_usuario_w
			;

			Update    pep_item_pendente
			set	nm_usuario = nm_usuario_w
			where	nr_seq_sae = NEW.nr_sequencia;

		end if;
end if;		
	
	if (coalesce(NEW.ie_tipo,'SAE') = 'SAPS') then
		ie_tipo_reg_w := 'SAPS';
	elsif (coalesce(NEW.ie_tipo,'SAE') = 'SAE') then
		ie_tipo_reg_w := 'SAE';
	end if;

if (NEW.dt_liberacao is null) then
		ie_tipo_w := ie_tipo_reg_w;
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'X' || ie_tipo_reg_w;
	end if;
		
if (ie_tipo_w	is not null)then
	CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, NEW.cd_pessoa_fisica, NEW.nr_atendimento, NEW.nm_usuario);
end if;
<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pe_prescricao_pend_atual() FROM PUBLIC;

CREATE TRIGGER pe_prescricao_pend_atual
	AFTER INSERT OR UPDATE ON pe_prescricao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pe_prescricao_pend_atual();

