SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pe_prescr_proc_update ON pe_prescr_proc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pe_prescr_proc_update() RETURNS trigger AS $BODY$
declare
qt_adm_w		bigint;

BEGIN
/* --> Rafael em 03/09/2007 x ADEP x cfe definido com Marcus <-- */

if (coalesce(OLD.ie_suspenso,'N') = 'N') and (NEW.ie_suspenso = 'S') then
	update	pe_prescr_proc_hor
	set	dt_suspensao = LOCALTIMESTAMP,
		nm_usuario_susp = NEW.nm_usuario_susp
	where	nr_seq_pe_prescr = NEW.nr_seq_prescr
	and	nr_seq_pe_proc = NEW.nr_sequencia
	and	dt_fim_horario is null
	and	dt_suspensao is null;
end if;

if (coalesce(NEW.nr_ocorrencia,0) = 0) and (NEW.cd_intervalo is not null) then
	NEW.nr_ocorrencia	:= Obter_ocorrencia_intervalo(NEW.cd_intervalo, 24,'O');
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pe_prescr_proc_update() FROM PUBLIC;

CREATE TRIGGER pe_prescr_proc_update
	BEFORE UPDATE ON pe_prescr_proc FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pe_prescr_proc_update();

