CREATE OR REPLACE FUNCTION trigger_fct_pfcs_after_upt_empresa() RETURNS trigger AS $BODY$
DECLARE
ds_log_w    varchar(4000);
qt_reg_w    smallint;

BEGIN
    if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'N')  then
