            || concat('old ie_situacao: ',          coalesce(OLD.ie_situacao,'null'))
            || concat(' | new ie_situacao: ',       coalesce(NEW.ie_situacao,'null'))
        );
        CALL pfcs_log_pck.pfcs_insert_org_struc_log(
            ie_type_p => 'UPDATE',
            nm_table_p => 'EMPRESA',
            ds_log_p => ds_log_w,
            nm_usuario_p => NEW.nm_usuario
        );
    end if;

<<Final>>
qt_reg_w := 0;
RETURN NEW;
END
