DROP TRIGGER IF EXISTS pj_regra_pre_cad ON pessoa_juridica CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pj_regra_pre_cad() RETURNS trigger AS $BODY$
declare

  v_check  bigint;

BEGIN

  if wheb_usuario_pck.get_ie_executar_trigger = 'S' then
    select coalesce(max(nr_sequencia),0)
      into STRICT v_check
      from regra_pre_cad_pj
     where cd_tipo_pessoa = NEW.cd_tipo_pessoa
       and ie_exige_aprov_cadastro = 'S';

    if v_check <> 0 then
      CALL wheb_mensagem_pck.exibir_mensagem_abort(1064319);
    end if;
  end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
