SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_aditamento_insert_update ON pls_contrato_aditamento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_aditamento_insert_update() RETURNS trigger AS $BODY$
declare
qt_subestipulante_w	bigint;
BEGIN

if (NEW.nr_seq_subestipulante is not null) then
	select count(1)
	into STRICT	qt_subestipulante_w
	from	pls_contrato a,
		pls_sub_estipulante b
	where	a.nr_sequencia = b.nr_seq_contrato
	and	b.nr_seq_contrato = NEW.nr_seq_contrato
	and	b.nr_sequencia = NEW.nr_seq_subestipulante;

	if (qt_subestipulante_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1144978);	
		/* O subestipulante informado nao esta cadastrado no contrato. */

	end if;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_aditamento_insert_update() FROM PUBLIC;

CREATE TRIGGER pls_aditamento_insert_update
	BEFORE INSERT OR UPDATE ON pls_contrato_aditamento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_aditamento_insert_update();

