DROP TRIGGER IF EXISTS pls_ame_regra_banda_befinsup ON pls_ame_regra_banda CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_ame_regra_banda_befinsup() RETURNS trigger AS $BODY$
declare

    qt_atributos_w bigint;
    qt_banda_w bigint;

pragma autonomous_transaction;
BEGIN

select count(1)
into STRICT qt_banda_w
from pls_ame_regra_banda a
where a.ie_tipo_banda = NEW.ie_tipo_banda
and a.nr_seq_regra = NEW.nr_seq_regra
and OLD.nr_sequencia <> NEW.nr_sequencia;

if (qt_banda_w > 0) then
  if (NEW.ie_tipo_banda = 1) then
    CALL wheb_mensagem_pck.exibir_mensagem_abort(1056922);
  elsif (NEW.ie_tipo_banda = 4) then
    CALL wheb_mensagem_pck.exibir_mensagem_abort(1056923);
  end if;
end if;

select count(1)
into STRICT qt_atributos_w
from pls_ame_regra_atrib a
where a.nr_seq_banda = OLD.nr_sequencia;

if (coalesce(OLD.ie_tipo_banda, 0) <> NEW.ie_tipo_banda and qt_atributos_w > 0) then
  CALL wheb_mensagem_pck.exibir_mensagem_abort(1056924);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_ame_regra_banda_befinsup() FROM PUBLIC;

CREATE TRIGGER pls_ame_regra_banda_befinsup
	BEFORE INSERT OR UPDATE ON pls_ame_regra_banda FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_ame_regra_banda_befinsup();

