DROP TRIGGER IF EXISTS pls_ame_regra_ger_contrato_att ON pls_ame_regra_ger_contrato CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_ame_regra_ger_contrato_att() RETURNS trigger AS $BODY$
declare

nr_seq_contrato_w	pls_contrato.nr_sequencia%type;
nr_seq_pagador_w	bigint;

BEGIN

if (NEW.nr_contrato is not null) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_contrato_w
	from	pls_contrato
	where	nr_contrato = NEW.nr_contrato;
	
	if (nr_seq_contrato_w is not null) then
		NEW.nr_seq_contrato := nr_seq_contrato_w;
	else
		CALL wheb_mensagem_pck.exibir_mensagem_abort(853575); --O contrato informado nao existe. Verifique!
	end if;
else
	NEW.nr_seq_contrato	:= null;
end if;

if (NEW.nr_contrato_principal is not null) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_contrato_w
	from	pls_contrato
	where	nr_contrato = NEW.nr_contrato_principal;
	
	if (nr_seq_contrato_w is not null) then
		NEW.nr_seq_contrato_principal := nr_seq_contrato_w;
	else
		CALL wheb_mensagem_pck.exibir_mensagem_abort(853575); --O contrato informado nao existe. Verifique!
	end if;
else
	NEW.nr_seq_contrato_principal	:= null;
end if;

if (NEW.nr_seq_pagador is not null) then
	select	count(1)
	into STRICT	nr_seq_pagador_w
	from	pls_contrato_pagador
	where	nr_sequencia = NEW.nr_seq_pagador;
	
	if (nr_seq_pagador_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1103531); --O pagador informado nao existe. Verifique!
	end if;
else
	NEW.nr_seq_pagador 	:= null;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
