DROP TRIGGER IF EXISTS pls_analise_conta_fat ON pls_analise_conta CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_analise_conta_fat() RETURNS trigger AS $BODY$
declare
C01 CURSOR(	nr_seq_analise_pc	pls_conta_pos_estabelecido.nr_seq_analise%type) FOR 
	SELECT	nr_sequencia 
	from	pls_conta 
	where	nr_sequencia in ( SELECT nr_seq_conta 
				 from	pls_conta_pos_estabelecido 
				 where	nr_seq_analise = nr_seq_analise_pc);
 
 
BEGIN 
 
if (NEW.ie_status = 'T') and (OLD.ie_status <> 'T') and (NEW.ie_origem_analise = '2') then 
	 
	for r_c01_w in C01(NEW.nr_sequencia) loop 
		 
		update	pls_conta 
		set	ie_status_fat = 'L' 
		where	nr_sequencia = r_c01_w.nr_sequencia;
		 
	end loop;
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_analise_conta_fat() FROM PUBLIC;

CREATE TRIGGER pls_analise_conta_fat
	AFTER UPDATE ON pls_analise_conta FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_analise_conta_fat();

