DROP TRIGGER IF EXISTS pls_atendimento_update ON pls_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_atendimento_update() RETURNS trigger AS $BODY$
declare
nr_seq_prot_atend_w	pls_protocolo_atendimento.nr_sequencia%type;
BEGIN
  BEGIN
if (NEW.nr_seq_segurado is not null) and (coalesce(OLD.ie_status, 'X') <> coalesce(NEW.ie_status, 'X')) and (OLD.ie_status in ('P','G')) and (NEW.ie_status in ('C', 'M')) then

	BEGIN
		select	nr_sequencia
		into STRICT	nr_seq_prot_atend_w
		from	pls_protocolo_atendimento
		where 	nr_seq_atend_pls	= NEW.nr_sequencia
		and 	ie_status		= 1;
	exception
	when others then
		nr_seq_prot_atend_w	:= null;
	end;

	if (nr_seq_prot_atend_w is not null) then
		update	pls_protocolo_atendimento
		set	ie_status	= 3,
			nm_usuario 	= NEW.nm_usuario,
			dt_atualizacao 	= LOCALTIMESTAMP
		where	nr_sequencia	= nr_seq_prot_atend_w;
	end if;
end if;

  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_atendimento_update() FROM PUBLIC;

CREATE TRIGGER pls_atendimento_update
	BEFORE UPDATE ON pls_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_atendimento_update();

