SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_chamado_insert ON pls_chamado CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_chamado_insert() RETURNS trigger AS $BODY$
declare
nr_seq_etapa_w	pls_tipo_chamado_etapa.nr_sequencia%type;
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_etapa_w
	from	pls_tipo_chamado_etapa
	where	nr_seq_tipo_chamado = NEW.nr_seq_tipo_chamado;
	
	if (nr_seq_etapa_w is null) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1199854); --Nao foi cadastrada nenhuma etapa para o tipo de chamado selecionado. Favor verifique.
	else
		CALL pls_chamado_pck.gerar_proxima_etapa(	nr_seq_chamado_p => NEW.nr_sequencia,
							nr_seq_tipo_chamado_p => NEW.nr_seq_tipo_chamado, 
							nr_seq_chamado_etapa_p => null, 
							nm_usuario_p => NEW.nm_usuario, 
							ie_commit_p => 'N');
	end if;
	
	CALL pls_chamado_pck.gerar_alerta(	nr_seq_chamado_p	=> NEW.nr_sequencia,
					ie_tipo_processo_p	=> '2',
					nr_seq_prestador_p	=> NEW.nr_seq_prestador,
					nr_seq_wsuite_usuario_p	=> NEW.nr_seq_wsuite_usuario,
					cd_estabelecimento_p	=> NEW.cd_estabelecimento,
					ie_tipo_atendimento_p	=> NEW.ie_tipo_atendimento,
					nm_usuario_p		=> NEW.nm_usuario,
					ie_commit_p		=> 'N');
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_chamado_insert() FROM PUBLIC;

CREATE TRIGGER pls_chamado_insert
	AFTER INSERT ON pls_chamado FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_chamado_insert();

