SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_competencia_atual ON pls_competencia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_competencia_atual() RETURNS trigger AS $BODY$
declare
qt_registro_w		integer;

pragma autonomous_transaction;

BEGIN
if (NEW.dt_mes_competencia is not null) then
	NEW.dt_mes_competencia := trunc(NEW.dt_mes_competencia, 'month');
end if;
if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S') then
	if (TG_OP = 'INSERT') or (TG_OP = 'UPDATE' and OLD.dt_mes_competencia_aux != NEW.dt_mes_competencia_aux) then
		select	count(1)
		into STRICT	qt_registro_w
		from	pls_competencia
		where	cd_estabelecimento	= NEW.cd_estabelecimento
		and	dt_mes_competencia_aux	= NEW.dt_mes_competencia_aux;
		
		if (qt_registro_w > 0) then
			-- A competencia #@DT_COMPETENCIA#@ ja foi cadastrada para este estabelecimento, favor informar outra competencia.

			CALL wheb_mensagem_pck.exibir_mensagem_abort( 1031372,'DT_COMPETENCIA=' || NEW.dt_mes_competencia_aux);
		end if;
	end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_competencia_atual() FROM PUBLIC;

CREATE TRIGGER pls_competencia_atual
	BEFORE INSERT OR UPDATE ON pls_competencia FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_competencia_atual();

