SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_conta_copartic_alt_pag ON pls_conta_coparticipacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_conta_copartic_alt_pag() RETURNS trigger AS $BODY$
declare
dt_pagador_w			timestamp	:= null;
ie_data_pagador_copartic_w	pls_parametros.ie_data_pagador_copartic%type;

BEGIN

if (NEW.nr_seq_pagador is null) then
	select	coalesce(max(ie_data_pagador_copartic),'N')
	into STRICT	ie_data_pagador_copartic_w
	from	pls_parametros
	where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

	if (ie_data_pagador_copartic_w = 'S') then
		select	max(dt_atendimento_referencia)
		into STRICT	dt_pagador_w
		from	pls_conta
		where	nr_sequencia	= NEW.nr_seq_conta;
	end if;

	NEW.nr_seq_pagador := pls_obter_pagador_benef(	NEW.nr_seq_segurado,
							coalesce(dt_pagador_w,LOCALTIMESTAMP),
							'3');
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_conta_copartic_alt_pag() FROM PUBLIC;

CREATE TRIGGER pls_conta_copartic_alt_pag
	BEFORE INSERT ON pls_conta_coparticipacao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_conta_copartic_alt_pag();

