SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_conta_log_tiss_update ON pls_conta CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_conta_log_tiss_update() RETURNS trigger AS $BODY$
declare

ie_tipo_evento_w	pls_monitor_tiss_alt.ie_tipo_evento%type;
qt_registros_fechado_w	bigint;
ie_tipo_segurado_w	pls_segurado.ie_tipo_segurado%type;
nr_seq_contrato_w	pls_segurado.nr_seq_contrato%type;
dt_evento_w		pls_protocolo_conta.dt_mes_competencia%type := null;
ie_data_protocolo_w	pls_monitor_tiss_param.ie_data_protocolo%type := 'N';

BEGIN
-- Finalidade:  Gravar log TISS para quando ocorrer fechamento da conta medica para posterior envio a ANS


ie_tipo_evento_w := null;

-- Se mudar de status

if (OLD.ie_status != NEW.ie_status) then
	
	-- Fechamento da conta medica

	if (NEW.ie_status = 'F') then
		ie_tipo_evento_w := 'FC';
	
	-- cancelamento de conta medica

	elsif (NEW.ie_status = 'C') then
		
		--Dentro da PCK ja esta tratado para nao enviar contas canceladas caso seja a primeira vez que esta sendo enviada

		ie_tipo_evento_w := 'CC';	
	end if;
end if;

-- se for pra registrar o evento (baseado no status da conta)

if (ie_tipo_evento_w is not null) then
	
	if (NEW.nr_seq_segurado is not null) then
		select	ie_tipo_segurado,
			nr_seq_contrato
		into STRICT	ie_tipo_segurado_w,
			nr_seq_contrato_w
		from	pls_segurado
		where	nr_sequencia = NEW.nr_seq_segurado;
	end if;
	
	if ( ie_tipo_evento_w = 'FC') then
		
			select 	coalesce(max(ie_data_protocolo),'N')
			into STRICT	ie_data_protocolo_w
			from 	pls_monitor_tiss_param
			where	cd_estabelecimento = NEW.cd_estabelecimento;
		
			if ( ie_data_protocolo_w = 'S') then
				
				select	max(coalesce(dt_recebimento, dt_mes_competencia))
				into STRICT	dt_evento_w
				from	pls_protocolo_conta
				where 	nr_sequencia = NEW.nr_seq_protocolo;
			end if;

	end if;
		
	-- para gravar somente beneficiarios que devem ser enviados

	if (ie_tipo_segurado_w in ('B', 'R')) and (nr_seq_contrato_w is not null) then
		-- OS2034202 Quando um update e realizado por processos de fora do Tasy, como JOBs, e necessario pegar o estabelecimento da conta medica

		if (wheb_usuario_pck.get_cd_estabelecimento is null) then		
			CALL wheb_usuario_pck.set_cd_estabelecimento(NEW.cd_estabelecimento);
		end if;
				
		CALL pls_tiss_gravar_log_monitor(	ie_tipo_evento_w, NEW.nr_sequencia, null,
						null, null, null,
						null, null, null,
						null, null, NEW.nm_usuario,
						coalesce(dt_evento_w, LOCALTIMESTAMP));
	end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_conta_log_tiss_update() FROM PUBLIC;

CREATE TRIGGER pls_conta_log_tiss_update
	BEFORE UPDATE ON pls_conta FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_conta_log_tiss_update();

